class Solution:
    def longestValidParentheses(self, s: str) -> int:
        max_so_far = 0
        l = [0 for char in s]
        #Change boolin in next part and put this inside the next part.
        #for i in range(len(s)):
        #    if i < len(s)-1:
        #        if s[i] == '(' and s[i+1] == ')':
        #            l[i] = 2
        
        
        for checked_length in range(len(s)):
            for i in range(len(s)-checked_length):
                if checked_length == 0 or l[i] > 0:
                    # this is doing too many chackes....
                    if i + l[i] < len(s):
                        if l[i+l[i]] >0:
                            l[i] += l[i+l[i]]
                            max_so_far = max(max_so_far,l[i])
                    if i > 0 and i + l[i] < len(s):
                        if l[i] >= checked_length:
                            if s[i-1] == '(' and s[i + l[i]] == ')':
                                l[i-1] = max (l[i-1],l[i] + 2)
                                max_so_far = max(max_so_far,l[i-1])
            if max_so_far < checked_length:
                break
        return max_so_far
                        
                        

