class Solution:
    # on v_4, maybe the i always stands for the right hand side of a string....
    def longestValidParentheses(self, s: str) -> int:
        max_so_far = 0
        l = [0 for char in s]
        i = 0
        while i < len(s):
            if l[i] == 0:
                if s[i] == ')':
                    i += 1
                    continue
                if s[i] == '(':
                    if i + 1 < len(s):
                        if s[i+1] == ')':
                            l[i] = 2
                            max_so_far = max( max_so_far, l[i])
                            length_of_stub = 2
                            i = i +2
                            #here i stands for right hand side of substring
                            while i < len(s):
                                if s[i] == '(':
                                    l[i] = -length_of_stub
                                    break
                                if s[i] == ')':
                                    if i - length_of_stub - 1 >= 0:
                                        if s[i - length_of_stub - 1] == '(':
                                            boo = l[i - length_of_stub - 1]
                                            length_of_stub = length_of_stub -boo +2
                                            l [i - length_of_stub + 1] = length_of_stub
                                            max_so_far = max(max_so_far,length_of_stub)
                                            i += 1
                        #else:
                            #None
                    

        return max_so_far
                        

