class Solution:
    # on v_4, maybe the i always stands for the right hand side of a string....
    def longestValidParentheses(self, s: str) -> int:
        max_so_far = 0
        l = [0 for char in s]
        #goal: on each i, will show the length of longest validparentheses ending at this point.
        for i in range(len(s)):
            if s[i] == ')':
                if i>0:
                    length_at_this_point = l[i-1]
                    if i - length_at_this_point - 1 >=0:
                        if s[i - length_at_this_point - 1] == '(':
                            if i - length_at_this_point - 1 >0:
                                extra_length = l[i - length_at_this_point - 2]
                            else:
                                extra_length = 0
                            l[i] = length_at_this_point + 2 + extra_length
                            max_so_far = max(max_so_far, l[i])
        return max_so_far
                        

